  class MySelect extends React.Component {
    handleChange = value => {
      // this is going to call setFieldValue and manually update values.colors
      this.props.onChange('colors', value);
    };
  

    render() {
     
      return (
        <div style={{ margin: '1rem 0' }}>
          <label htmlFor="color">
