sourcesContent:
import React ,{useState , useEffect} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
import Header from '../../Header';
import './edit.css';
import {useDispatch, useSelector} from "react-redux";
import { selectEducations, addEducation, deleteEducation, updateEducation} from "../../../../redux/slices/educationSlice";
import { selectCvs, addCv, deleteCv} from "../../../../redux/slices/cvSlice";
import { Formik } from "formik";
import * as Yup from "yup";
import ReactNotification from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'
import { store } from 'react-notifications-component';
import {Dialog,Button,DialogActions,DialogContent,DialogContentText,DialogTitle } from '@material-ui/core';
// Import the main component
import { Viewer } from '@react-pdf-viewer/core'; // install this library
// Plugins
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout'; // install this library
// Import the styles
import '@react-pdf-viewer/core/lib/styles/index.css';
import '@react-pdf-viewer/default-layout/lib/styles/index.css';
// Worker
import { Worker } from '@react-pdf-viewer/core'; // install this library
import dateFormat from 'dateformat';
import Camera from 'react-dom-camera';
const EducationScreen = ({history}) =>{

    
const [educations,  err, reload] =useSelector(selectEducations);
const [cvs,  err1, reload1] =useSelector(selectCvs);
const [education,setEducation] = useState(Object);
    const [user, setUser]= useState(Object);
    
    const [username, setName]= useState("");
    const [dateBirth, setBirth]= useState("");
    const [country, setCountry]= useState("");
    const [address, setAddress]= useState("");
    const [numTel, setTel]= useState("");
    const [error, setError]= useState("");
    const [profilePicture, setProfile]= useState("");
    const [coverPicture, setCover]= useState("");
    const [cv, setCv]= useState("");
    const [url,setUrl] = useState("");
    const [urlC,setUrlc] = useState("");

    const [followers,setFollowers] = useState([]);
    const [numbersF,setNumbersF] = useState(0);
    const [numbersFby,setNumbersFby] = useState(0);

    const [followers_page,setFollowers_page] = useState([]);
    const [numbersPage,setNumbersPage] = useState(0);

    const [open, setOpen] = React.useState(false);
    const [open1, setOpen1] = React.useState(false);
    const [open2, setOpen2] = React.useState(false);
    const [open3, setOpen3] = React.useState(false);
    const [open4, setOpen4] = React.useState(false);
    const [open5, setOpen5] = React.useState(false);

    const [image,setImage] = useState("");

    // Create new plugin instance
  const defaultLayoutPluginInstance = defaultLayoutPlugin();
  
  // for onchange event
  const [pdfFile, setPdfFile]=useState(null);
  const [pdfFileError, setPdfFileError]=useState('');

  // for submit event
  const [viewPdf, setViewPdf]=useState(null);

  const dateB=dateFormat(user.dateBirth, "mmmm dS, yyyy");

  const CoolButton = ({ onClick }) => (
  <button onClick={onClick} type="button">
    <strong>TakePhoto</strong>  
  </button>
);


    const handleClickOpen = () => {
      setOpen(true);
    };
  
    const handleClose = () => {
      setOpen(false);
    };

    const handleClickOpen1 = () => {
        setOpen1(true);
      };
    
      const handleClose1 = () => {
        setOpen1(false);
      };

      const handleClickOpen2 = () => {
        setOpen2(true);
      };
    
      const handleClose2 = () => {
        setOpen2(false);
      };

      const handleClickOpen3 = () => {
        setOpen3(true);
      };
    
      const handleClose3 = () => {
        setOpen3(false);
      };
      const handleClickOpen4 = () => {
        setOpen4(true);
      };
    
      const handleClose4 = () => {
        setOpen4(false);
      };
      const handleClickOpen5 = () => {
        setOpen5(true);
      };
    
      const handleClose5 = () => {
        setOpen5(false);
      };


     


    useEffect(() => {
        if(!localStorage.getItem("authToken")){
            history.push("/login")
        }

        const headers = {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem("authToken")}`
          }

        axios.get(`https://aaweni.herokuapp.com/api/auth/details_user`, {
            headers: headers
        })
        .then((response) => {
            setUser(response.data.data);
         console.log(user)
        })
        .catch((error) => {
          console.log(error)
        })

        

        if(url){
            axios.put(`https://aaweni.herokuapp.com/api/auth/updateprofilepicture`,{
                profilePicture:url
            }, {
                headers: headers
            })
            .then((response) => {
            console.log(response)
            })
            .catch((error) => {
                console.log(error)
            })
        }

        if(urlC){
            axios.put(`https://aaweni.herokuapp.com/api/auth/updatecoverpicture`,{
                coverPicture:urlC
            }, {
                headers: headers
            })
            .then((response) => {
            console.log(response)
            })
            .catch((error) => {
                console.log(error)
            })
        }

        if(image){
            axios.put(`https://aaweni.herokuapp.com/api/auth/updateprofilepicture`,{
                profilePicture:image
            }, {
                headers: headers
            })
            .then((response) => {
            console.log(response)
            })
            .catch((error) => {
                console.log(error)
            })
        }
      

            /**get all followers */

            axios.get(`https://aaweni.herokuapp.com/followuser/getFollowers`, {
                headers: headers
            })
            .then((response) => {
                setFollowers(response.data);
            console.log(response.data)
            })
            .catch((error) => {
            console.log(error)
            })


            axios.get(`https://aaweni.herokuapp.com/followuser/numbers`, {
                headers: headers
            })
            .then((response) => {
                setNumbersF(response.data);
            console.log(response.data)
            })
            .catch((error) => {
            console.log(error)
            })

            axios.get(`https://aaweni.herokuapp.com/followuser/numbersf`, {
                headers: headers
            })
            .then((response) => {
                setNumbersFby(response.data);
            console.log(response.data)
            })
            .catch((error) => {
            console.log(error)
            })

            /** end followers */

            /**get all followers page */

            axios.get(`https://aaweni.herokuapp.com/followpage/getFollowers`, {
                headers: headers
            })
            .then((response) => {
                setFollowers_page(response.data);
            console.log(response.data)
            })
            .catch((error) => {
            console.log(error)
            })


            axios.get(`https://aaweni.herokuapp.com/followpage/numbers`, {
                headers: headers
            })
            .then((response) => {
                setNumbersPage(response.data);
            console.log(response.data)
            })
            .catch((error) => {
            console.log(error)
            })

            }
           
    ,[history,url,urlC,user]);//,user

   

    const updateHandler = async (e) =>{
       // e.preventDefault();
        const headers = {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem("authToken")}`
          }
          console.log(e.target.value)

        axios.put(`https://aaweni.herokuapp.com/api/auth/update`,{username,dateBirth,numTel,country,address}, {
            headers: headers
          })
          .then((response) => {
           console.log(response)
           store.addNotification({
            title: "Wonderful!",
            message: "Your personnel information has been updated with succes! check your profile",
            type: "success",
            insert: "top",
            container: "top-right",
            animationIn: ["animate__animated", "animate__fadeIn"],
            animationOut: ["animate__animated", "animate__fadeOut"],
            dismiss: {
              duration: 3000,
              onScreen: true
            }
          });
          })
          .catch((error) => {
            console.log(error)
          })
        
    }

    const onChangeProfile = e =>{
        setProfile(e.target.files[0]);
        //console.log(profilePicture)
    }

    
    const updateProfilePictureHandler = async (e) =>{
           const data = new FormData()
            data.append("file",profilePicture)
            data.append("upload_preset","projetRN")
            data.append("cloud_name","sabrine")
       fetch("https://api.cloudinary.com/v1_1/sabrine/image/upload",{
           method:"post",
           body:data
       })
       .then(res=>res.json())
       .then(data=>{
          setUrl(data.url)
          console.log(data);
       })
       .catch(err=>{
           console.log(err)
       })

       const headers = {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("authToken")}`
      }
      console.log(url)

        axios.put(`https://aaweni.herokuapp.com/api/auth/updateprofilepicture`,{
            profilePicture:url
        }, {
            headers: headers
        })
        .then((response) => {
        console.log(response)
        })
        .catch((error) => {
            console.log(error)
        })
         
     }

     const onChangeCover = e =>{
        setCover(e.target.files[0]);
    }

     const updateCoverPictureHandler = async (e) =>{
        const data = new FormData()
         data.append("file",coverPicture)
         data.append("upload_preset","projetRN")
         data.append("cloud_name","sabrine")
    fetch("https://api.cloudinary.com/v1_1/sabrine/image/upload",{
        method:"post",
        body:data
    })
    .then(res=>res.json())
    .then(data=>{
       setUrlc(data.url)
       console.log(data);
    })
    .catch(err=>{
        console.log(err)
    })

    const headers = {
     'Content-Type': 'application/json',
     Authorization: `Bearer ${localStorage.getItem("authToken")}`
   }
   console.log(url)

     axios.put(`https://aaweni.herokuapp.com/api/auth/updateprofilepicture`,{
         profilePicture:url
     }, {
         headers: headers
     })
     .then((response) => {
     console.log(response)
     })
     .catch((error) => {
         console.log(error)
     })
     
      
  }

  const dispatch =useDispatch();

  /* const AddEHandler = async (e) =>{
    const headers = {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("authToken")}`
      }

    axios.post(`/education/add`,{school,fieldStudy,startYear,endYear,visible,description}, {
        headers: headers
      })
      .then((response) => {
       console.log(response.data.data);
       dispatch(addEducation(response.data.data));
      })
      .catch((error) => {
        console.log(error)
      })
   }*/

   const deleteEHandler = (id) =>async () => {
    const headers = {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("authToken")}`
      }

    axios.delete(`https://aaweni.herokuapp.com/education/delete/${id}`, {
        headers: headers
      })
      .then((response) => {
       dispatch(deleteEducation(id));
      })
      .catch((error) => {
        console.log(error)
      })
   }

   const editclick = (e) =>{
       setEducation(e);
   }

    const deleteHandler = (id) =>async () => {
        const headers = {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem("authToken")}`
          }
    
        axios.delete(`https://aaweni.herokuapp.com/cv/delete/${id}`, {
            headers: headers
          })
          .then((response) => {
           dispatch(deleteCv(id));
          })
          .catch((error) => {
            console.log(error)
          })
       }

// onchange event
const fileType=['application/pdf'];



    return (
        <>
        <Header/>
        <body>
        <ReactNotification/>
        {/** Edit profile picture */}



        <div>
      
      <Dialog
          open={open1}
          onClose={handleClose1}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
      >
          <DialogActions>
          <Button onClick={handleClose1} color="primary" autoFocus>
          X
          </Button>
          </DialogActions>
          <DialogTitle id="alert-dialog-title"></DialogTitle>
          <DialogContent>
          <DialogContentText id="alert-dialog-description">
          <div class="profile user-profile bg-white rounded-2xl -mt-4">
            <div class="profiles_banner">
                        <img src={user.coverPicture} alt="Cover Picture" />
                        
                    </div>

                <div class="profiles_content">

                    <div class="profile_avatar">
                        <div class="profile_avatar_holder"> 
                            <img src={user.profilePicture} alt=""/>
                        </div>
                        <div class="user_status status_online"></div>
                        <div class="icon_change_photo" hidden> <ion-icon name="camera" class="text-xl"></ion-icon> </div>
                    </div>

                    <div class="profile_info">
                        <h1> {user.username} </h1>
                    </div>

                </div>
                <form onSubmit={(e)=>{
                                            e.preventDefault()
                                            updateProfilePictureHandler(e)
                                        }}>
                <div>
                    <input style={{width:'600px'}} type="file" placeholder="Profile Picture" id="profilePicture" required onChange={onChangeProfile} tabIndex={1} class="bg-gray-100 h-12 mt-2 px-3 rounded-md w-full"/>
                </div>
                <div>
                    <button type="submit" class="bg-blue-600 font-semibold p-2 mt-5 rounded-md text-center text-white">Update</button>
                </div> 
                </form>        
            </div>
               
          </DialogContentText>
          </DialogContent>
          
      </Dialog>
      </div>


  
{/** end */}



{/** camera */}



<div>
      
      <Dialog
          open={open5}
          onClose={handleClose5}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
      >
          <DialogActions>
          <Button onClick={handleClose5} color="primary" autoFocus>
          X
          </Button>
          </DialogActions>
          <DialogTitle id="alert-dialog-title"></DialogTitle>
          <DialogContent>
          <DialogContentText id="alert-dialog-description">
          <br></br><br></br><br></br><br></br><br></br><br></br>
          <div class="profile user-profile bg-white rounded-2xl -mt-4">
          <div class="profiles_content">

            <div class="profile_avatar">
                <div class="profile_avatar_holder"> 
                    <img src={user.profilePicture} alt=""/>
                </div>
                <div class="user_status status_online"></div>
                <div class="icon_change_photo" hidden> <ion-icon name="camera" class="text-xl"></ion-icon> </div>
            </div>

            <div class="profile_info">
                <h1> {user.username} </h1>
            </div>

            </div>
            </div>
            <Camera
            captureButtonRenderer={onClick => <CoolButton onClick={onClick} />}
            onTakePhoto={image =>
            console.log(setImage(image), 'do whatever you want with the image')
            }
          />
         
               
          </DialogContentText>
          </DialogContent>
          
      </Dialog>
      </div>


  
{/** end camera */}



{/** edit cover picture */}


<div>
      
      <Dialog
          open={open2}
          onClose={handleClose2}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
      >
          <DialogActions>
          <Button onClick={handleClose2} color="primary" autoFocus>
          X
          </Button>
          </DialogActions>
          <DialogTitle id="alert-dialog-title"></DialogTitle>
          <DialogContent>
          <DialogContentText id="alert-dialog-description">
          <div class="profile user-profile bg-white rounded-2xl -mt-4">
            <div class="profiles_banner">
                        <img src={user.coverPicture} alt="Cover Picture"/>
                        
                    </div>

                <div class="profiles_content">

                    <div class="profile_avatar">
                        <div class="profile_avatar_holder"> 
                            <img src={user.profilePicture} alt=""/>
                        </div>
                        <div class="user_status status_online"></div>
                        <div class="icon_change_photo" hidden> <ion-icon name="camera" class="text-xl"></ion-icon> </div>
                    </div>

                    <div class="profile_info">
                        <h1> {user.username} </h1>
                    </div>

                </div>
                <form onSubmit={(e)=>{
                                            e.preventDefault()
                                            updateCoverPictureHandler(e)
                                        }}>
                <div>
                    <input style={{width:'600px'}} type="file" placeholder="Cover Picture" id="coverPicture" required onChange={onChangeCover} tabIndex={1} class="bg-gray-100 h-12 mt-2 px-3 rounded-md w-full"/>
                </div>
                <div>
                    <button type="submit" class="bg-blue-600 font-semibold p-2 mt-5 rounded-md text-center text-white">Update</button>
                </div> 
                </form>        
            </div>
               
          </DialogContentText>
          </DialogContent>
          
      </Dialog>
      </div>

{/** end */}

{/** add education */}

<Formik
initialValues={{
    school:'',
    fieldStudy:'',
    startYear:'',
    endYear:'',
    visible:'',
    description:''}} 
validationSchema={Yup.object({
        school: Yup.string()
          .min(3, "Minimum 3 characters")
          .max(50, "Maximum 50 character")
          .required('Required field'),
        fieldStudy: Yup.string()
          .min(3, "Minimum 3 characters")
          .max(50, "Maximum 50 character")
          .required('Required field'),
        startYear: Yup.string()
        .matches(/^[0-9]+$/, "Invalid Year")
        .min(4, 'Invalid Year: must be exactly 4 digits')
        .max(4, 'Invalid Year: must be exactly 4 digits').required('Required field'),
        endYear: Yup.string()
        .matches(/^[0-9]+$/, "Invalid Year")
        .min(4, 'Invalid Year: must be exactly 4 digits')
        .max(4, 'Invalid Year: must be exactly 4 digits').required('Required field'),
        visible:Yup.string()
        .required('Required field'),
      })}
onSubmit={ async (values)=>{
            
    const valuesToSend = { ...values};
    const headers = {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("authToken")}`
      }

    axios.post(`https://aaweni.herokuapp.com/education/add`,valuesToSend, {
        headers: headers
      })
      .then((response) => {
       console.log(response.data.data);
       dispatch(addEducation(response.data.data));
       store.addNotification({
        title: "Wonderful!",
        message: "Your education details have been added with succes! check your profile",
        type: "success",
        insert: "top",
        container: "top-right",
        animationIn: ["animate__animated", "animate__fadeIn"],
        animationOut: ["animate__animated", "animate__fadeOut"],
        dismiss: {
          duration: 3000,
          onScreen: true
        }
      });
   
      })
      .catch((error) => {
        console.log(error)
      })
  }}
>
    {formik => (

<div>
      
<Dialog
    open={open3}
    onClose={handleClose3}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
>
    <DialogActions>
    <Button onClick={handleClose3} color="primary" autoFocus>
    X
    </Button>
    </DialogActions>
    <DialogTitle id="alert-dialog-title">Add education</DialogTitle>
    <DialogContent>
    <DialogContentText id="alert-dialog-description">
    <form onSubmit={formik.handleSubmit} >
                                    
                                        <div class="grid lg:grid-cols-2 gap-3">
                                            <div class="input-group has-validation">
                                                <label htmlFor="school" class="mb-0"> School <span style={{color:'red'}}>*</span></label>
                                                <input type="text" placeholder="School" id="school" {...formik.getFieldProps('school')} aria-describedby="inputGroupPrepend3 validationServerUsernameFeedback" class="bg-gray-100 h-12 mt-2 px-3 rounded-md w-full" />
                                                {formik.touched.school && formik.errors.school ? (
                                                        <div id="school" style={{color:'red', fontSize:'13px'}} class="form-control is-invalid" >
                                                        {formik.errors.school}.
                                                        </div>    
                                                ) : null}
                                            </div>

                                            <div>
