import React ,{useState , useEffect} from 'react';
import {Link} from 'react-router-dom';
import Header from '../../Header';
import axios from 'axios';
import { Formik } from "formik";
import * as Yup from "yup";
import ReactNotification from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'
import { store } from 'react-notifications-component';
import { useDispatch ,useSelector} from 'react-redux';
import { fetchMyPages } from '../../../../redux/slices/mypagesSlice';
const CreatePageScreen = ({history,match}) =>{
    //const [page, setPage]= useState(Object);
    const dispatch =useDispatch();
    const [categories, setCategories]= useState([]);
    const id = match.params.id;
    const page = useSelector((state) =>
        state.pages.pages.find((item) => item._id === id)
        );

    useEffect(() => {
        if(!localStorage.getItem("authToken")){
            history.push("/login")
        }    
        
        const config = {
            headers: {
                "Content-Type":"appliation/json",
                Authorization: `Bearer ${localStorage.getItem("authToken")}`
            }
        }

        axios.get(`https://aaweni.herokuapp.com/category/getAll`, config)
        .then((response) => {
         setCategories(response.data);
         console.log(response.data)
        })
        .catch((error) => {
          console.log(error)
        })

        const id = match.params.id;

                axios.get(`https://aaweni.herokuapp.com/page/details/${id}`, config)
                  .then((response) => {
                   //setPage(response.data);
                   console.log(page)
                  })
                  .catch((error) => {
                    console.log(error)
                  })
               
            }
           
    ,[history]);
    

    console.log(page.name)

    
    return (
        <>
        <Header/>
        <body>
        <ReactNotification/>
        <div class="main_content">
            <div class="mcontainer">
            <h1 class="lg:text-2xl text-xl font-semibold mb-6" > Update your page </h1>



            <Formik
           
initialValues={{
    name:page?.name,
    type:page?.type,
