import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { loadModels, getFullFaceDescription, createMatcher } from '../../../api/face';
import axios from "axios";

// Import image to test API


// Import face profile

 let  JSON_PROFILE = "";
 

// Initial State
const INIT_STATE = {
  imageURL: "https://res.cloudinary.com/espritnn/image/upload/v1621196984/astro-mona_jbcjs6.webp",
  fullDesc: null,
  detections: null,
  descriptors: null,
  match: null
};

class ImageInput   extends Component { 
  constructor(props) {
    super(props);
    INIT_STATE.imageURL= localStorage.getItem('profile-pic');
   console.log (INIT_STATE.imageURL);
    this.state = { ...INIT_STATE, faceMatcher: null };
  }

  componentWillMount = async () => {



  axios.get('https://aaweni.herokuapp.com/helloworld.json')
  .then(function (response)   {

    JSON_PROFILE = response.data;

 
 })
 .catch(function (error) {
   console.log(error);
 });

 await loadModels();
 this.setState({ faceMatcher: await createMatcher(JSON_PROFILE) });
 await this.handleImage(this.state.imageURL);

 };

  handleImage = async (image = this.state.imageURL) => {
    await getFullFaceDescription(image).then(fullDesc => {
      if (!!fullDesc) {
        this.setState({
          fullDesc,
          detections: fullDesc.map(fd => fd.detection),
          descriptors: fullDesc.map(fd => fd.descriptor)
        });
        axios.post("https://aaweni.herokuapp.com/reco/"+localStorage.getItem('email'), ""+this.state.descriptors)
        .then(res => {
         
          console.log(res);
          console.log(res.data);
        })
        window.location.href = "https://3aweni.netlify.app/postsprofile";
      }
    });

    if (!!this.state.descriptors && !!this.state.faceMatcher) {
      let match = await this.state.descriptors.map(descriptor =>
        this.state.faceMatcher.findBestMatch(descriptor)
      );
      this.setState({ match });
      
    }
  };

  handleFileChange = async event => {
    this.resetState();
    await this.setState({
      imageURL: URL.createObjectURL(event.target.files[0]),
      loading: true
    });
    this.handleImage();
  };

  resetState = () => {
    this.setState({ ...INIT_STATE });
  };

  render() {
    const { imageURL, detections, match } = this.state;

    let drawBox = null;
    if (!!detections) {
      drawBox = detections.map((detection, i) => {
        let _H = detection.box.height;
        let _W = detection.box.width;
        let _X = detection.box._x;
        let _Y = detection.box._y;
        return (
          <div key={i}>
            <div
              style={{
                position: 'absolute',
                border: 'solid',
                borderColor: 'blue',
                height: _H,
                width: _W,
                transform: `translate(${_X}px,${_Y}px)`
              }}
            >
              {!!match ? (
                <p
                  style={{
                    backgroundColor: 'blue',
                    border: 'solid',
                    borderColor: 'blue',
                    width: _W,
                    marginTop: 0,
                    color: '#fff',
                    transform: `translate(-3px,${_H}px)`
                  }}
                >
                  {match[i]._label}
                </p>
              ) : null}
            </div>
          </div>
        );
      });
    }

    return (
      <div>
        <input
          id="myFileUpload"
          type="file"
          onChange={this.handleFileChange}
          accept=".jpg, .jpeg, .png"
        />
        <div style={{ position: 'relative' }}>
          <div style={{ position: 'absolute' }}>
            <img src={imageURL} alt="imageURL" />
          </div>
          {!!drawBox ? drawBox : null}
        </div>
      </div>
    );
  }
}

export default withRouter(ImageInput);
