/* eslint-disable jsx-a11y/alt-text */

import axios from "axios";
import { useEffect, useRef, useState } from "react";
import Moment from "react-moment";
import FormInput from "./FormInput";
import io from "socket.io-client";
import CommentItem from "./CommentItem";
import Loading from "../Problem/loading.gif";
import Header from'../Header';
import HeaderWithoutLeftPanel from "../HeaderWithoutLeftPanel";
import LeftPanelProblem from "../LeftPanelProblem";
/* eslint-disable jsx-a11y/anchor-is-valid */
export default function ProblemDetail(props) {
  const id = props.match.params.id;
  const [problemWanted, setproblemWanted] = useState([]);
  const [socket, setSocket] = useState(null);
  const [comments, setComments] = useState([]);
  const [loading, setloading] = useState(false);
  const [page, setPage] = useState(1);
  const pageEnd = useRef();
  useEffect(() => {
    axios
      .get("https://aaweni.herokuapp.com/pi/postRoute/problemdetail/" + id)
      .then((res) => setproblemWanted(res.data.problem));
    const socket = io.connect("wss://aaweni.herokuapp.com");
    setSocket(socket);
    return () => socket.close();
  }, [id]);
  //console.log(problemWanted);
  useEffect(() => {
    setloading(true);
    axios
      .get("https://aaweni.herokuapp.com/pi/commentRoute/comment/" + id + "?limit=" + page * 5)
      .then((res) => {
        setComments(res.data.comments);
        setloading(false);
      })
      .catch((err) => console.log(err));
  }, [id, page]);

  // RealTime
  useEffect(() => {
    if (socket) {
      socket.emit("joinRoom", id);
    }
  }, [socket, id]);

  useEffect(() => {
    if (socket) {
      socket.on("sendCommentToClient", (msg) => {
        setComments([msg, ...comments]);
        console.log(msg);
      });
      return () => socket.off("sendCommentToClient");
    }
  }, [socket, comments]);

  useEffect(() => {
    if (socket) {
      socket.on("commentDeleted", (msg) => {
        //setComments([msg, ...comments]);
        axios
          .get("https://aaweni.herokuapp.com/pi/commentRoute/comment/" + id + "?limit=" + page * 5)
          .then((res) => {
            setComments(res.data.comments);
            setloading(false);
          });
        console.log(msg);
      });
      return () => socket.off("commentDeleted");
    }
  }, [socket, comments, id, page]);

  useEffect(() => {
    if (socket) {
      socket.on("commentUpdated", (msg) => {
        //setComments([msg, ...comments]);
        console.log("after updating: " + msg);
        axios
          .get("https://aaweni.herokuapp.com/pi/commentRoute/comment/" + id + "?limit=" + page * 5)
          .then((res) => {
            setComments(res.data.comments);
            setloading(false);
          });
      });
      return () => socket.off("commentUpdated");
    }
  }, [socket, comments, id, page]);

  //infinity scroll
  useEffect(() => {
