import React ,{useState , useEffect} from 'react';
import {Link} from 'react-router-dom';
import Header from '../../Header';
import axios from 'axios';
import { Formik } from "formik";
import * as Yup from "yup";
import ReactNotification from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'
import { store } from 'react-notifications-component';
import { fetchMyPages } from '../../../../redux/slices/mypagesSlice';
import { useDispatch } from 'react-redux';
import { fetchPages } from '../../../../redux/slices/pagesSlice';
import HeaderWithoutLeftPanel from '../../HeaderWithoutLeftPanel';
import LeftPanelPage from '../../LeftPanelPage';
const CreatePageScreen = ({history}) =>{

    const dispatch =useDispatch();
    const [categories, setCategories]= useState([]);

    useEffect(() => {
        if(!localStorage.getItem("authToken")){
            history.push("/login")
        }    
        
        const config = {
            headers: {
                "Content-Type":"appliation/json",
                Authorization: `Bearer ${localStorage.getItem("authToken")}`
            }
        }

        axios.get(`https://aaweni.herokuapp.com/category/getAll`, config)
        .then((response) => {
         setCategories(response.data);
         console.log(response.data)
        })
        .catch((error) => {
          console.log(error)
        })
        
            }
           
    ,[history]);
    

         

    
    return (
        <>
          <HeaderWithoutLeftPanel/>
        <LeftPanelPage/>
        <body>
        <ReactNotification/>
        <div class="main_content">
            <div class="mcontainer">
            <h1 class="lg:text-2xl text-xl font-semibold mb-6" > Create a new page </h1>



            <Formik
initialValues={{
    name:'',
    type:'',
    numTel:'',
    country:'',
    address:'',
    profilePicture:'',
    coverPicture:'',
    description:''}} 
validationSchema={Yup.object({
        name: Yup.string()
          .min(3, "Minimum 3 characters")
          .max(50, "Maximum 50 character")
          .required('Required field'),
          country: Yup.string()
          .min(3, "Minimum 3 characters")
          .max(50, "Maximum 50 character")
          .required('Required field'),
          address: Yup.string()
          .min(3, "Minimum 3 characters")
          .max(50, "Maximum 50 character")
          .required('Required field'),
        numTel: Yup.string()
        .matches(/^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/, "Invalid number")
        .required('Required field'),
